package com.example.users

import android.annotation.SuppressLint
import android.content.Context
import android.os.Bundle
import android.util.Log
import android.widget.Toast
import androidx.appcompat.app.AppCompatActivity
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.example.users.databinding.ActivityMainBinding
import com.google.android.material.dialog.MaterialAlertDialogBuilder
import com.google.android.material.textfield.TextInputEditText

class MainActivity : AppCompatActivity(), OnClickListener {

    private lateinit var userAdapter: UsersAdapter
    private lateinit var linearLayoutManager: RecyclerView.LayoutManager

    private lateinit var binding: ActivityMainBinding

    @SuppressLint("ResourceType")
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityMainBinding.inflate(layoutInflater)
        setContentView(binding.root)

        val preference = getPreferences(Context.MODE_PRIVATE)

        val isFirstTime = preference.getBoolean(getString(R.string.sp_first_time), true)
        Log.i("SP", "${getString(R.string.sp_first_time)} = $isFirstTime")

        if (isFirstTime) {
            val dialogView = layoutInflater.inflate(R.layout.dialog_register, null)
            MaterialAlertDialogBuilder(this)
                .setTitle(R.string.dialog_title)
                .setView(dialogView)
                .setCancelable(false)
                .setPositiveButton(R.string.dialog_confirm) { dialogInteface, i ->
                    val userName =
                        dialogView.findViewById<TextInputEditText>(R.id.etUserName).text.toString()
                    with(preference.edit()) {
                        putBoolean(getString(R.string.sp_first_time), false)
                        putString(getString(R.id.sp_username), userName)
                            .apply()
                    }
                }
                .setNegativeButton("cancelar", null)
                .show()
        }

        userAdapter = UsersAdapter(getUsers(), this)
        linearLayoutManager = LinearLayoutManager(this)

        binding.recyclerView.apply {
            setHasFixedSize(true)
            layoutManager = linearLayoutManager
            adapter = userAdapter
        }
    }

    private fun getUsers(): MutableList<Users> {
        val users = mutableListOf<Users>()
        val jose = Users(
            1,
            "jose",
            "david",
            "data:image/jpeg;base64,",
        )
        val veronica = Users(
            2,
            "veronica",
            "isabel",
            "data:image/png;base64,",
        )
        val santiago = Users(
            3,
            "santiago",
            "castrillon",
            "data:image/jpeg;base64,",
        )
        val yaneth = Users(
            4,
            "luz",
            "yaneth",
            "data:image/png;base64,",
        )

        users.add(santiago)
        users.add(yaneth)
        users.add(jose)
        users.add(veronica)
        users.add(santiago)
        users.add(yaneth)
        users.add(jose)
        users.add(veronica)
        users.add(santiago)
        users.add(yaneth)
        users.add(jose)
        users.add(veronica)
        users.add(santiago)
        users.add(yaneth)
        users.add(jose)
        users.add(veronica)
        users.add(santiago)
        return users
    }

    override fun onClick(user: Users, position: Int) {
        Toast.makeText(this, "$position: ${user.getFullName()}", Toast.LENGTH_LONG).show()
    }
}
